#include <iostream>
using namespace std;

int a[1002][1002];
int n, num = 1;

void Round(int r){
    for(int i = n + r - 1; i >= n - r; i--){
        a[n - r][i] = num;
        num++;
    }
    for(int i = n - r + 1; i <= n + r; i++){
        a[i][n - r] = num;
        num++;
    }
    for(int i = n - r + 1; i <= n + r; i++){
        a[n + r][i] = num;
        num++;
    }
    for(int i = n + r - 1; i >= n - r; i--){
        a[i][n + r] = num;
        num++;
    }
}

int main(){
    int r = 1; 
    cin >> n;
    for(int i = 1; i <= n; i++){
        Round(i);
    }
    for(int i = 0; i <= 2 * n; i++){
        for(int j = 0; j <= 2 * n; j++){
            cout << a[i][j] << " ";
        }
        cout << "\n";
    }
}
